mapper.select:
select

from post
where 1=1

  and status = #{status,jdbcType=BIGINT}


  and username = #{username,jdbcType=VARCHAR}

order by `id` desc
limit #{start,jdbcType=BIGINT}, #{count,jdbcType=INTEGER}

select count(id)
from post
where 1=1

  and status = #{status,jdbcType=BIGINT}


  and username = #{username,jdbcType=VARCHAR}
